INSERT INTO `exam_topic` VALUES ('254', '5', '兄弟两人钓了60条鱼，哥哥钓鱼的条数是弟弟的3倍，求两人各钓了多少条鱼？哥哥：弟弟：', '<p><strong><span style=\"font-size:14px;font-family:宋体;\">兄弟两人钓了<span lang=\"EN-US\">60</span>条鱼，哥哥钓鱼的条数是弟弟的<span lang=\"EN-US\">3</span>倍，求两人各钓了多少条鱼？</span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">哥哥：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">弟弟：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p>', '<p><img src=\"http://files.dodoedu.com/xue/2b68af2bb1223c52e0700c204218b59f.\" /></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">弟弟：</span><span style=\"font-size:16px;line-height:150%\">60</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">÷（</span><span style=\"font-size:16px;line-height:150%\">1+3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）</span><span style=\"font-size:16px;line-height:150%\">=60</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">÷</span><span style=\"font-size:16px;line-height:150%\">4=15</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">（条）</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">哥哥：</span><span style=\"font-size:16px;line-height:150%\">15</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">×</span><span style=\"font-size:16px;line-height:150%\">3=45</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">（条）</span></p>', '45[p]15', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375926287');
INSERT INTO `exam_topic` VALUES ('255', '5', '小明、小红共只数15棵,小明植树的棵树比小红的2倍少3棵，求两人各植树多少棵？小红：小明：', '<p><strong><span style=\"font-size:14px;font-family:宋体;\">小明、小红共只数</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">15</span><span style=\"font-size:14px;font-family:宋体;\">棵,小明植树的棵树比小红的</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">2</span><span style=\"font-size:14px;font-family:宋体;\">倍少</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">3</span><span style=\"font-size:14px;font-family:宋体;\">棵，求两人各植树多少棵？</span></strong></p><p>小红：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></p><p>小明：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></p>', '<p><img src=\"http://files.dodoedu.com/xue/d26f70e935b5d18a217c028e650777fa.\" /></p><p><span style=\"font-size:16px;font-family:宋体\">小红：（</span><span style=\"font-size:16px\">15+3</span><span style=\"font-size:16px;font-family:宋体\">）÷（</span><span style=\"font-size:16px\">1+2</span><span style=\"font-size:16px;font-family:宋体\">）</span><span style=\"font-size:16px\">=6</span><span style=\"font-size:16px;font-family:宋体\">（棵）</span></p><p><span style=\"font-size:16px;font-family:宋体\">小明：</span><span style=\"font-size:16px\">15-6=9</span><span style=\"font-size:16px;font-family:宋体\">（棵）</span></p>', '6[p]9', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375926353');
INSERT INTO `exam_topic` VALUES ('256', '5', '某学校五、六年级共有学生180人，五年级的学生人数是六年级学生人数的2倍，求五、六年级各有学生多少人？五年级：六年级：', '<p><strong><span style=\"font-size:14px;font-family:宋体;\">某学校五、六年级共有学生</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">180</span><span style=\"font-size:14px;font-family:宋体;\">人，五年级的学生人数是六年级学生人数的</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">2</span><span style=\"font-size:14px;font-family:宋体;\">倍，求五、六年级各有学生多少人？</span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">五年级：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">六年级：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p>', '<p><img src=\"http://files.dodoedu.com/xue/54e76b739181d559a8177353c4d97c0b.\" /></p><p><span style=\"font-family:宋体\">六年级：</span>180<span style=\"font-family:宋体\">÷（</span>1+2<span style=\"font-family:宋体\">）</span>=60<span style=\"font-family:宋体\">（人）</span></p><p><span style=\"font-size:14px;font-family:宋体\">五年级：</span><span style=\"font-size:14px;font-family:&#39;times new roman&#39;\">60</span><span style=\"font-size:14px;font-family:宋体\">×</span><span style=\"font-size:14px;font-family:&#39;times new roman&#39;\">2=120</span><span style=\"font-size:14px;font-family:宋体\">（人）</span></p>', '120[p]60', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375926499');
INSERT INTO `exam_topic` VALUES ('257', '5', '种小麦和水稻共1110公顷，水稻公顷数比小麦的4倍多60公顷，求小麦和水稻各种了多少公顷？小麦：水稻：', '<p><strong><span style=\"font-size:14px;font-family:宋体;\">种小麦和水稻共</span><st1:chmetcnv unitname=\"公顷\" sourcevalue=\"1110\" hasspace=\"False\" negative=\"False\" numbertype=\"1\" tcsc=\"0\" w:st=\"on\"><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">1110</span><span style=\"font-size:14px;font-family:宋体;\">公顷</span></st1:chmetcnv><span style=\"font-size:14px;font-family:宋体;\">，水稻公顷数比小麦的</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">4</span><span style=\"font-size:14px;font-family:宋体;\">倍多</span><st1:chmetcnv unitname=\"公顷\" sourcevalue=\"60\" hasspace=\"False\" negative=\"False\" numbertype=\"1\" tcsc=\"0\" w:st=\"on\"><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">60</span><span style=\"font-size:14px;font-family:宋体;\">公顷</span></st1:chmetcnv><span style=\"font-size:14px;font-family:宋体;\">，求小麦和水稻各种了多少公顷？</span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">小麦：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">水稻：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p>', '<p><img src=\"http://files.dodoedu.com/xue/f084c956759daefdef645a6c0fc7ce4e.\" /></p><p><span style=\"font-family:宋体\">小麦：（</span>1110-60<span style=\"font-family:宋体\">）÷（</span>1+4<span style=\"font-family:宋体\">）</span>=210<span style=\"font-family:宋体\">（公顷）</span></p><p><span style=\"font-family:宋体\">水稻：</span>1110-210=900<span style=\"font-family:宋体\">（公顷）</span></p><p style=\"text-indent:14px\"><span style=\"font-family:宋体\">或：</span>210<span style=\"font-family:宋体\">×</span>4+60=900<span style=\"font-family:宋体\">（公顷）</span></p>', '210[p]900', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375926566');
INSERT INTO `exam_topic` VALUES ('258', '5', '图书室有科技书、故事书共64本，其中科技书是故事书的3倍，科技书和故事书各有多少本？故事书：科技书：', '<p><strong><span style=\"font-size:14px;font-family:宋体;\">图书室有科技书、故事书共</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">64</span><span style=\"font-size:14px;font-family:宋体;\">本，其中科技书是故事书的</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">3</span><span style=\"font-size:14px;font-family:宋体;\">倍，科技书和故事书各有多少本？</span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">故事书：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">科技书：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p>', '<p><img src=\"http://files.dodoedu.com/xue/e09cc50a3838f85d35ba40e713f546ad.\" title=\"1.gif\" /></p><p><span style=\"font-family:宋体\">故事书：</span>64<span style=\"font-family:宋体\">÷（</span>1+3<span style=\"font-family:宋体\">）</span>=16<span style=\"font-family:宋体\">（本）</span></p><p><span style=\"font-family:宋体\">科技书：</span>16<span style=\"font-family:宋体\">×</span>3=48<span style=\"font-family:宋体\">（本）</span></p><p><br /></p>', '16[p]48', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375926833');
INSERT INTO `exam_topic` VALUES ('259', '5', '玉华乡原有水田750公顷，旱田570公顷，要把多少公顷旱田改为水田，才能使水田的公顷数是旱田的2倍？旱田改为水田公顷', '<p><strong><span style=\"font-family:宋体\">玉华乡原有水田750公顷，旱田570公顷，要把多少公顷旱田改为水田，才能使水田的公顷数是旱田的2倍？</span></strong></p><p><font face=\"宋体\"><b>旱田改为水田</b></font><img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /><strong>公顷</strong><br /></p>', '<p><strong><span style=\"font-size:14px;font-family:宋体;\">先考虑水田是旱田的</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">2</span><span style=\"font-size:14px;font-family:宋体;\">倍时，水田和旱田各有多少，再还原</span></strong><span style=\"font-size:16px;font-family:宋体;\">。</span></p><p><span style=\"font-size:16px;font-family:宋体;\"><img src=\"http://files.dodoedu.com/xue/1db5ee57dc08febebf33946e84145c07.\" title=\"1.gif\" /></span></p><p><span style=\"font-size:16px;font-family:宋体;\"></span></p><p><span style=\"font-family:宋体\">旱田：（</span>750+570<span style=\"font-family:宋体\">）÷（</span>1+2<span style=\"font-family:宋体\">）</span>=440<span style=\"font-family:宋体\">（公顷）</span></p><p><span style=\"font-family:宋体\">所以旱田改为水田有：</span>570-440=130<span style=\"font-family:宋体\">（公顷）</span></p><p><br /></p>', '130', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375926966');
INSERT INTO `exam_topic` VALUES ('260', '5', '甲乙两个仓库共有水泥200吨，甲仓库水泥的吨数是乙仓库水泥吨数的4倍，求甲乙两仓库各有水泥多少吨？甲：乙：', '<p><strong><span style=\"font-family:宋体\">甲乙两个仓库共有水泥</span></strong><strong>200</strong><strong><span style=\"font-family:宋体\">吨，甲仓库水泥的吨数是乙仓库水泥吨数的</span></strong><strong>4</strong><strong><span style=\"font-family:宋体\">倍，求甲乙两仓库各有水泥多少吨？</span></strong></p><p><strong><span style=\"font-family:宋体\">甲：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p><p><strong><span style=\"font-family:宋体\">乙：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p>', '<p><img src=\"http://files.dodoedu.com/xue/d907c3a6219417e101b9a91b02104cf3.\" title=\"1.gif\" /></p><p><span style=\"font-size:16px;font-family:宋体\">甲：</span><span style=\"font-size:16px\">200</span><span style=\"font-size:16px;font-family:宋体\">÷（</span><span style=\"font-size:16px\">1+4</span><span style=\"font-size:16px;font-family:宋体\">）×</span><span style=\"font-size:16px\">4=160</span><span style=\"font-size:16px;font-family:宋体\">（吨）</span></p><p><span style=\"font-size:16px;font-family:宋体\">乙：</span><span style=\"font-size:16px;font-family:&#39;times new roman&#39;\">200</span><span style=\"font-size:16px;font-family:宋体\">÷（</span><span style=\"font-size:16px;font-family:&#39;times new roman&#39;\">1+4</span><span style=\"font-size:16px;font-family:宋体\">）</span><span style=\"font-size:16px;font-family:&#39;times new roman&#39;\">=40</span><span style=\"font-size:16px;font-family:宋体\">（吨）</span><span style=\"font-size:16px;font-family:宋体\">或</span><span style=\"font-size:16px;font-family:&#39;times new roman&#39;\">160</span><span style=\"font-size:16px;font-family:宋体\">÷</span><span style=\"font-size:16px;font-family:&#39;times new roman&#39;\">4=40</span><span style=\"font-size:16px;font-family:宋体\">（吨）</span></p>', '160[p]40', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375927094');
INSERT INTO `exam_topic` VALUES ('261', '5', '甲乙两数的和是70，甲数比乙数的3倍少10 ，求甲数是多少？甲数：', '<p><strong><span style=\"font-size:14px;font-family:宋体;\">甲乙两数的和是</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">70</span><span style=\"font-size:14px;font-family:宋体;\">，甲数比乙数的</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">3</span><span style=\"font-size:14px;font-family:宋体;\">倍少</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&#39;times new roman&#39;;\">10 </span><span style=\"font-size:14px;font-family:宋体;\">，求甲数是多少？</span></strong></p><p><strong><span style=\"font-size:14px;font-family:宋体;\">甲数：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></span></strong></p>', '<p><img src=\"http://files.dodoedu.com/xue/3c7ecf79153ae5f09d24c3bac8031b4f.\" title=\"1.gif\" /></p><p><span style=\"font-family:宋体\">乙：（</span>70+10<span style=\"font-family:宋体\">）÷（</span>3+1<span style=\"font-family:宋体\">）</span>=20</p><p><span style=\"font-family:宋体\">甲：</span>20<span style=\"font-family:宋体\">×</span>3-10=50</p>', '50', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375927171');
INSERT INTO `exam_topic` VALUES ('262', '5', '小梅的奶奶养了56只鸡和鸭，鸡的只数是鸭的6倍，小梅的奶奶样的鸡和鸭各有多少只？鸡：鸭：', '<p><strong><span style=\"font-family:宋体\">小梅的奶奶养了</span></strong><strong>56</strong><strong><span style=\"font-family:宋体\">只鸡和鸭，鸡的只数是鸭的</span></strong><strong>6</strong><strong><span style=\"font-family:宋体\">倍，小梅的奶奶样的鸡和鸭各有多少只？</span></strong></p><p><strong>鸡：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></strong></p><p><strong>鸭：<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" /></strong></p>', '<p><img src=\"http://files.dodoedu.com/xue/524be5ffb13dc371010d1203edf43405.\" title=\"1.gif\" /></p><p><span style=\"font-family:宋体\"><span style=\"font-family:宋体;\">鸡：</span>8<span style=\"font-family:宋体;\">×</span>6=48<span style=\"font-family:宋体;\">（只）</span></span></p><p><span style=\"font-family:宋体\">鸭：</span>56<span style=\"font-family:宋体\">÷（</span>1+6<span style=\"font-family:宋体\">）</span>=8<span style=\"font-family:宋体\">（只）</span></p>', '48[p]8', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '1', '0', '1375927305');
INSERT INTO `exam_topic` VALUES ('263', '5', '甲乙两个油桶，加油桶有油85升，乙油桶有油29升。甲桶的油倒入乙桶多少升，才能使甲桶的油是乙桶油的2倍？倒入升', '<p><strong><span style=\"font-family:宋体\">甲乙两个油桶，加油桶有油85升，乙油桶有油29升。甲桶的油倒入乙桶多少升，才能使甲桶的油是乙桶油的2倍？</span></strong></p><p><strong>倒入<img name=\"tiankongImg\" src=\"static/ueditorAll/themes/default/images/tiankong.gif\" />升</strong></p>', '<p><span style=\"font-family:宋体\">甲、乙两桶总量不变，先考虑甲是乙的2倍时，甲、乙各有多少？再求甲、乙原来的数？</span></p><p><img src=\"http://files.dodoedu.com/xue/b0cef8c5848d2c9c8ec2b775d1a3abb2.\" title=\"1.gif\" /></p><p><span style=\"font-family:宋体\">乙：（</span>85+29<span style=\"font-family:宋体\">）÷（</span>1+2<span style=\"font-family:宋体\">）</span>=38<span style=\"font-family:宋体\">（升）</span></p><p><span style=\"font-family:宋体\">所以，甲给乙：</span>38-29=9<span style=\"font-family:宋体\">（升）</span></p>', '9', '1', '1', '0', '0', '1', '8', '', 'a35832177803845470085', '1', '0', '0', '1375927404');
